Ext.define('RobotDriver.view.BaseControlStick', {
    extend: 'Ext.Container',
    alias: 'widget.basecontrolstick',

    requires: [
        'RobotDriver.view.BaseControlStickViewModel',
        'Ext.Container'
    ],

    viewModel: {
        type: 'basecontrolstick'
    },
    height: 150,
    userCls: 'stick-container',
    width: 150,
    maxHeight: 150,
    maxWidth: 150,
    defaultListenerScope: true,

    items: [
        {
            xtype: 'container',
            height: 80,
            userCls: 'stick-inner-container',
            width: 80,
            html: '<div class="stick" id="virctrlstickRight"></div>',
            margin: '35 35 0 35',
            maxHeight: 80,
            maxWidth: 80
        },
        {
            xtype: 'container',
            height: 30,
            itemId: 'label',
            userCls: 'stick-label'
        }
    ],
    listeners: {
        painted: 'onContainerPainted'
    },

    onContainerPainted: function(sender, element, eOpts) {
        if(this.init){
            return;
        }else{
            this.init = true;
        }

        if(element.component.config.label){
            this.setLabel(element.component.config.label);
        }else{
            this.setLabel('');
        }

        let stickRange = 50;
        this.virtualControllerStickLeft = createStick(element.dom.getElementsByClassName('stick')[0], stickRange, 'left', function(pos){

            var x = Math.round(((pos.x/stickRange) * 500) + 500);
            var y = 1000 - Math.round(((pos.y/stickRange) * 500) + 500);

            //console.log('stick move!', x, y);
            this.fireEvent('change', x, y);
        }.bind(this));
    },

    setLabel: function(text) {
        this.label = text;
        this.queryById('label').setHtml(text);
    },

    getLabel: function() {
        return this.label;
    }

});