    controlMove: function(control, direction) {
        let parent = this.queryById('controlItems');

        let currentIndex = parent.items.items.indexOf(control);

        if(direction === 'up'){
            currentIndex--;
        }else{
            currentIndex++;
        }

        parent.remove(control, false);
        parent.insert(currentIndex, control);

    },

